import React from 'react';
import './Testimonials.css'
import { Container, Row, Col} from 'react-bootstrap';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

export default function  Testimonials() {

  const customArrowStyles = {
    color: 'red', // Cor da seta anterior e seguinte
  };

  const CustomPrevArrow = ({ styles, onClick }) => (
    <div
      className="custom-prev-arrow-class"
      style={styles}
      onClick={onClick}
    >
      {/* Ícone ou conteúdo personalizado para a seta anterior */}
    </div>
  );

  const CustomNextArrow = ({ styles, onClick }) => (
    <div
      className="custom-next-arrow-class"
      style={styles}
      onClick={onClick}
    >
      {/* Ícone ou conteúdo personalizado para a seta seguinte */}
    </div>
  );

  const settings = {
    dots: true,
    dotsClass: 'slick-dots custom-dots-class', 
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    prevArrow: <CustomPrevArrow styles={customArrowStyles} />, 
    nextArrow: <CustomNextArrow styles={customArrowStyles} />
  };

  return (
    <div className='backgroundColor'>
      <Container id="Testimonials">
      <Row style={{paddingTop:"3rem"}}>
      <Col className="d-flex justify-content-center"> 
        <div className='cardPricingTitle'>
            <h1>
            Trusted by Thousands of <br /> Happy Customer
            </h1>
            <p>
            These are the stories of our customers who have joined us with great <br /> pleasure when using this crazy feature.            </p>
        </div>
        </Col>
      </Row>
<Row>
  <Col>
  <Slider {...settings}>
      <div>
        <h3>Testemunho 1</h3>
        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
